function handlePreloader(){$(".preloader").length&&$(".preloader").delay(500).fadeOut(500)}$(window).on("load",function(){handlePreloader()});var theToggle=document.getElementById("toggle");function hasClass(e,a){return new RegExp(" "+a+" ").test(" "+e.className+" ")}function addClass(e,a){hasClass(e,a)||(e.className+=" "+a)}function removeClass(e,a){var s=" "+e.className.replace(/[\t\r\n]/g," ")+" ";if(hasClass(e,a)){for(;0<=s.indexOf(" "+a+" ");)s=s.replace(" "+a+" "," ");e.className=s.replace(/^\s+|\s+$/g,"")}}function toggleClass(e,a){var s=" "+e.className.replace(/[\t\r\n]/g," ")+" ";if(hasClass(e,a)){for(;0<=s.indexOf(" "+a+" ");)s=s.replace(" "+a+" "," ");e.className=s.replace(/^\s+|\s+$/g,"")}else e.className+=" "+a}theToggle.onclick=function(){return toggleClass(this,"on"),!1},$("#toggle").on("click",function(){$(this).hasClass("on"),$("#menu").fadeToggle()});